T = (0,0);
S = (2,0);
R = (2,sqrt(21));
draw(R--S--T--R);
draw(rightanglemark(R,S,T,10));
label("$T$",T,SW);
label("$S$",S,SE);
label("$R$",R,NE);
label("$5$",(R+T)/2,NW);
[/asy]

